import java.util.*;

public class Solution_v3 {

	public static void main(String[] args) {

		// String s = "abcabcd";
		String s = "";
		System.out.println("String s: aacbccabcccaaaadbaacdbbaacdbcccbc ... (9999 chars)");
		System.out.printf("Lognest substring length: %d\n", lengthOfLogestSubstring(s));

	} // end main

	public static int lengthOfLogestSubstring(String s) {

		int maxLength = 0;
		for (int right = 0, left = 0; right < s.length(); right++) {
			int indexOfFirstAppearanceInSubstring = s.indexOf(s.charAt(right), left);
			if (indexOfFirstAppearanceInSubstring != right ) {          // it exists in substring!
				left = indexOfFirstAppearanceInSubstring + 1;
			}

			maxLength = Math.max(maxLength, right - left + 1);
		}
		return maxLength;
	}
}

